import { getEnvironmentUrl } from '@dynatrace-sdk/app-environment';
export default async function () {
  return await getEnvironmentUrl();
}

dt.statistics.ui.anomaly_detection.AutoA.query:
timeseries requests=avg(dt.service.request.response_time), by: { dt.entity.service }, filter:{
  dt.entity.service in [fetch dt.entity.service 
  |filter belongs_to[dt.entity.cloud_application_namespace][0] in [
  fetch dt.entity.cloud_application_namespace
  | filter in(entity.name,"google-trip-id-service")
  | fields id
] | fields id
]
}
| fieldsAdd dt.entity.service.name = entityName(dt.entity.service)
| sort arrayAvg(requests) desc
| limit 20

